// ignore_for_file: prefer_const_constructors

import 'dart:ui';

import 'package:flutter/material.dart';

class Calculator extends StatefulWidget {
  const Calculator({Key? key}) : super(key: key);

  @override
  State<Calculator> createState() => _CalculatorState();
}

class _CalculatorState extends State<Calculator> {
  // Helps to collect data from TextFields
  TextEditingController principalController = TextEditingController();
  TextEditingController rateController = TextEditingController();
  TextEditingController termController = TextEditingController();
  String displayResult = "";
  bool isChecked = true;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        backgroundColor: Colors.transparent,
        title: const Text(
          "BMI Calculator",
        ),
      ),
      body: Container(
        decoration: BoxDecoration(
            image: DecorationImage(
                image: NetworkImage(
                    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6Farp6podW7WbTtQRExAtQFJ3nO_42TwPRgJgdldPtG3iaqm1yBvvJMTX4Kb8i9zIvKY&usqp=CAU"),
                fit: BoxFit.cover,
                colorFilter: ColorFilter.mode(
                    Colors.black.withOpacity(0.1), BlendMode.darken))),
        child: ListView(
          children: [
            Center(
              child: Image(
                image: NetworkImage(
                    'data:image/jpeg;base64,'),
                width: 300,
                height: 300,
              ),
            ),
            TextField(
              keyboardType: TextInputType.number,
              controller: principalController,
              decoration: InputDecoration(
                  labelText: "Mass",
                  hintText: "Enter in Kg",
                  border: OutlineInputBorder(
                      borderRadius: BorderRadius.circular(10))),
            ),
            SizedBox(
              height: 10,
            ),
            TextField(
              keyboardType: TextInputType.number,
              controller: rateController,
              decoration: InputDecoration(
                  labelText: "Height",
                  hintText: "Enter in meters",
                  border: OutlineInputBorder(
                      borderRadius: BorderRadius.circular(10))),
            ),
            SizedBox(
              height: 10,
            ),
            // TextField(
            //   keyboardType: TextInputType.number,
            //   controller: termController,
            //   decoration: InputDecoration(
            //       labelText: "Term",
            //       hintText: "Enter Term",
            //       border: OutlineInputBorder(
            //           borderRadius: BorderRadius.circular(10))),
            // ),
            SizedBox(
              height: 10,
            ),
            // Row(
            //   children: [
            //     Checkbox(
            //         value: isChecked,
            //         onChanged: (b) {
            //           setState(() {
            //             isChecked = b!;
            //           });
            //         }),
            //     Text("Are you agree with Terms")
            //   ],
            // ),
            Row(
              children: [
                Expanded(
                  child: Container(
                    margin: EdgeInsets.all(5),
                    child: ElevatedButton(
                        onPressed: () {
                          setState(() {
                            displayResult = _calculateResult();
                          });
                        },
                        child: Text(
                          "Calculate",
                          style: TextStyle(fontSize: 20),
                        )),
                  ),
                ),
              ],
            ),
            SizedBox(
              width: 20,
            ),
            Row(
              children: [
                Expanded(
                  child: ElevatedButton(
                      onPressed: () {
                        setState(() {
                          principalController.text = "";
                          rateController.text = "";
                          termController.text = "";
                          displayResult = "";
                          isChecked = false;
                        });
                      },
                      child: Text(
                        "Reset",
                        style: TextStyle(fontSize: 20),
                      )),
                ),
              ],
            ),
            Text(
              displayResult,
              style: TextStyle(fontWeight: FontWeight.bold),
            )
          ],
        ),
      ),
    );
  }

  String _calculateResult() {
    double principal = double.parse(principalController.text);
    double rate = double.parse(rateController.text);
    // double term = double.parse(termController.text);

    double totalAmount = principal / (rate * rate);

    String result = 'Your BMI is $totalAmount';
    return result;
  }
}
